import UIKit
import CoreData

class CDDoctorWorker
{
  // MARK: Business Logic
  
    func deleteConsult(entity: CDConsultData, completionHandler: (success: Bool) -> Void) {
        do {
            CDCoreDataStack.sharedInstance.managedObjectContext.deleteObject(entity)
            try CDCoreDataStack.sharedInstance.managedObjectContext.save()
            completionHandler(success: true)
        } catch {
            print(error)
            completionHandler(success: false)
        }
    }
}
