import UIKit

// MARK: Connect View, Interactor, and Presenter

extension CDUserViewController: CDUserPresenterOutput
{
  override func prepareForSegue(segue: UIStoryboardSegue, sender: AnyObject?)
  {
    if segue.identifier == "Consulta" {
        router.passDataToNextScene(segue)
    }
  }
}

extension CDUserInteractor: CDUserViewControllerOutput
{
}

extension CDUserPresenter: CDUserInteractorOutput
{
}

class CDUserConfigurator
{
  // MARK: Object lifecycle
  
  class var sharedInstance: CDUserConfigurator
  {
    struct Static {
      static var instance: CDUserConfigurator?
      static var token: dispatch_once_t = 0
    }
    
    dispatch_once(&Static.token) {
      Static.instance = CDUserConfigurator()
    }
    
    return Static.instance!
  }
  
  // MARK: Configuration
  
  func configure(viewController: CDUserViewController)
  {
    let router = CDUserRouter()
    router.viewController = viewController
    
    let presenter = CDUserPresenter()
    presenter.output = viewController
    
    let interactor = CDUserInteractor()
    interactor.output = presenter
    
    viewController.output = interactor
    viewController.router = router
  }
}
